sys_variable_value.value:
(function(outputs, steps, params, stepResult, assertEqual) {
    // add test script here
    describe("ChangeHelper._createStdChange tests", function() {

        it('should create a new standard change and return the number and sys_id', function() {

            var params = {
                "template_id": "404e063a1b895a503f31a9b6624bcb89",
                "requested_by": "ab7289",
                "modtrak_number": "12345"
            };

            var result = (new x_neyu_modtrak.ChangeHelper())._createStdChange(params);

            var re = /[a-f0-9]{32}/g;
            expect(result.data.sys_id).toBeDefined();
            expect(result.data.number).toBeDefined();
            expect(re.test(result.data.sys_id)).toBe(true);
            expect(result.data.number.indexOf("CHG") !== -1).toBe(true);
        });

        it('should remove the template_id from params then call StandardChangeProcess', function() {
            var params = {
                "template_id": "404e063a1b895a503f31a9b6624bcb89",
                "requested_by": "ab7289",
                "modtrak_number": "12345"
            };
            var process = global.StandardChangeProcess;

            var changeSpy = spyOn(process, "newChangeProcess");
            changeSpy.and.callThrough();

            (new x_neyu_modtrak.ChangeHelper())._createStdChange(params);

            expect(changeSpy).toHaveBeenCalledWith(
                "404e063a1b895a503f31a9b6624bcb89", {
                    "requested_by": "ab7289",
                    "modtrak_number": "12345"
                });
        });

        it("should create a valid standard change", function() {
            var params = {
                "template_id": "404e063a1b895a503f31a9b6624bcb89",
                "requested_by": "ab7289",
                "modtrak_number": "12345"
            };

            var result = (new x_neyu_modtrak.ChangeHelper())._createStdChange(params);

            expect(result.data.sys_id).toBeDefined();
            var gr = new GlideRecord("change_request");
            expect(gr.get("sys_id", result.data.sys_id)).toBe(true);
            expect(gr.isValidRecord()).toBe(true);
            expect(gr.getValue("type")).toBe("standard");
        });

        it("should set the correlation display and id on the change request", function() {
            var params = {
                "template_id": "404e063a1b895a503f31a9b6624bcb89",
                "requested_by": "ab7289",
                "modtrak_number": "12345"
            };

            var result = (new x_neyu_modtrak.ChangeHelper())._createStdChange(params);

            expect(result.data.sys_id).toBeDefined();

            var gr = new GlideRecord("change_request");
            expect(gr.get("sys_id", result.data.sys_id)).toBe(true);

            var key = x_neyu_modtrak.ModTrakAPI.CONSTANTS.CORRELATION_DISPLAY;
            expect(gr.getValue("correlation_display")).toBe(key);
            expect(gr.getValue("correlation_id")).toBe(params["modtrak_number"]);
        });

    });

})(outputs, steps, params, stepResult, assertEqual);
// uncomment the next line to execute this script as a jasmine test
jasmine.getEnv().execute();
