sys_variable_value.value:
(function(outputs, steps, params, stepResult, assertEqual) {
    // add test script here
    describe("ChangeHelper._createChange tests", function() {

        var params = {
            "description": "test post",
            "requested_by": "tp1406",
            "short_description": "test",
            "start_date": "2024-10-21 00:00:00",
            "requested_by_date": "2024-10-30 00:00:00",
            "service_offering": "NYU Wi-Fi",
            "state": "-5",
            "type": "normal",
            "modtrak_number": "4321"
        };

        it('should return a valid sys_id and number when given valid inputs', function() {

            var result = (new x_neyu_modtrak.ChangeHelper())._createChange(params);

            expect(result.data).toBeDefined();
            expect(result.data.sys_id).toBeDefined();
            expect(result.data.number).toBeDefined();
            var re = /[a-f0-9]{32}/g;
            expect(re.test(result.data.sys_id)).toBe(true);
            expect(result.data.number.indexOf("CHG") !== -1).toBe(true);
        });

        it('should create a valid normal change', function() {

            var result = (new x_neyu_modtrak.ChangeHelper())._createChange(params);

            expect(result.data.sys_id).toBeDefined();
            var gr = new GlideRecord("change_request");
            expect(gr.get("sys_id", result.data.sys_id)).toBe(true);
            expect(gr.isValidRecord()).toBe(true);
            expect(gr.getValue("type")).toBe(params["type"]);
        });

        it('should set the correlation_display and correlation_id on the change', function() {

            var result = (new x_neyu_modtrak.ChangeHelper())._createChange(params);

            expect(result.data.sys_id).toBeDefined();

            var gr = new GlideRecord("change_request");
            expect(gr.get("sys_id", result.data.sys_id)).toBe(true);

            var key = x_neyu_modtrak.ModTrakAPI.CONSTANTS.CORRELATION_DISPLAY;
            expect(gr.getValue("correlation_display")).toBe(key);
            expect(gr.getValue("correlation_id")).toBe(params["modtrak_number"]);
        });

        it('should return an error if change model is not valid', function() {
            var newParams = Object.assign({}, params);
            newParams.type = "asdf";

            var result = (new x_neyu_modtrak.ChangeHelper())._createChange(newParams);

            expect(result).toEqual({
                error: true,
                errorMessage: "Unable to locate change model for type",
                status: 400
            });
        });
    });

})(outputs, steps, params, stepResult, assertEqual);
// uncomment the next line to execute this script as a jasmine test
jasmine.getEnv().execute();
