sys_variable_value.value:
(function(outputs, steps, params, stepResult, assertEqual) {

    describe("ChangeHelper.createChange Method tests", function() {

        it('should call _createStdChange if template_id is present', function() {
            var request = {
                "template_id": "404e063a1b895a503f31a9b6624bcb89",
                "requested_by": "ab7289",
                "modtrak_number": "12345"
            };

            var api = new x_neyu_modtrak.ChangeHelper();
            var changeSpy = spyOn(api, "_createChange");
            var stdChangeSpy = spyOn(api, "_createStdChange");

            api.createChange(request);
            expect(changeSpy).not.toHaveBeenCalled();
            expect(stdChangeSpy).toHaveBeenCalled();
        });

        it('should call _createChange if template_id is not present', function() {

            var request = {
                "description": "test post",
                "requested_by": "tp1406",
                "short_description": "test",
                "start_date": "2024-10-21 00:00:00",
                "requested_by_date": "2024-10-30 00:00:00",
                "service_offering": "NYU Wi-Fi",
                "state": "-5",
                "type": "standard",
                "modtrak_number": "4321"
            };

            var api = new x_neyu_modtrak.ChangeHelper();
            spyOn(api, "_createChange");
            spyOn(api, "_createStdChange");

            api.createChange(request);
			expect(api._createChange).toHaveBeenCalled();
			expect(api._createStdChange).not.toHaveBeenCalled();
        });

    });

})(outputs, steps, params, stepResult, assertEqual);
